import React from 'react';
import './Login.css'; // Tái sử dụng file CSS cho các style chung

const EmailConfirmation = ({ onToggleView, userEmail }) => {
  return (
    <div className="login-container">
      <h2 className="login-title">Please confirm your email address</h2>
      <div className="confirmation-content">
        <ul>
          <li>We have sent a confirmation link to: <b>{userEmail}</b></li>
          <li>Confirmation email may take up to 5 minutes to appear in your inbox.</li>
          <li>Please confirm through the link in the email to create your account.</li>
        </ul>
      </div>
      <button className="login-btn">Resend email</button>
      <a href="#" className="back-link" onClick={onToggleView}>
        ← Back to Login
      </a>
    </div>
  );
};

export default EmailConfirmation;
