import React from 'react';
import './Login.css';

const EmailConfirmation = ({ onToggleView, userEmail }) => {
  return (
    <div className="login-container">
      <h2 className="login-title">Vui lòng xác nhận email của bạn</h2>
      <div className="confirmation-content">
        <p>Chúng tôi đã gửi một liên kết xác nhận đến:</p>
        <p><b>{userEmail}</b></p>
        <p>Email có thể mất vài phút để xuất hiện trong hộp thư đến của bạn.</p>
        <p>Vui lòng click vào liên kết để tạo tài khoản.</p>
      </div>
      <button className="login-btn">Gửi lại email</button>
      <a href="#" className="back-link" onClick={onToggleView}>
        ← Trở lại Đăng nhập
      </a>
    </div>
  );
};

export default EmailConfirmation;
