import React, { useState } from 'react';
import './Login.css';

// Nhận props onToggleView
const Signup = ({ onToggleView }) => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert("Mật khẩu và xác nhận mật khẩu không khớp!");
      return;
    }
    console.log('Thông tin đăng ký:', { firstName, lastName, email, password });
  };

  return (
    <div className="login-container">
      {/* Nút quay lại */}
      <a href="#" onClick={onToggleView} className="back-link">
        ← Trở về Đăng nhập
      </a>
      <h2 className="login-title">Tạo tài khoản</h2>
      <form onSubmit={handleSubmit} className="login-form">
        {/* ... các trường nhập liệu ... */}
        <button type="submit" className="login-btn">Tạo tài khoản</button>
      </form>
    </div>
  );
};

export default Signup;
