import React, { useState } from 'react';
import Login from './components/Login';
import Signup from './components/Signup';
import EmailConfirmation from './components/EmailConfirmation'; // Import component mới
import './index.css';

function App() {
  const [isLoginView, setIsLoginView] = useState(true);
  const [isEmailConfirmationView, setIsEmailConfirmationView] = useState(false);
  const [userEmail, setUserEmail] = useState('');

  const toggleToSignup = () => {
    setIsLoginView(false);
    setIsEmailConfirmationView(false);
  };

  const toggleToLogin = () => {
    setIsLoginView(true);
    setIsEmailConfirmationView(false);
  };

  const handleSignupSuccess = (email) => {
    setIsEmailConfirmationView(true);
    setIsLoginView(false);
    setUserEmail(email); // Lưu email để truyền sang component xác nhận
  };

  const renderView = () => {
    if (isLoginView) {
      return <Login onToggleView={toggleToSignup} />;
    }
    if (isEmailConfirmationView) {
      return <EmailConfirmation onToggleView={toggleToLogin} userEmail={userEmail} />;
    }
    return <Signup onToggleView={toggleToLogin} onSignupSuccess={handleSignupSuccess} />;
  };

  return <div className="App">{renderView()}</div>;
}

export default App;
